  /**
   * @private
   * @type {number|null}
   */
  this.tourTimeStart_ = null;
  /**
   * @private
   * @type {number|null}
   */
  this.tourTimeRun_ = null;
  /**
   * @private
   * @type {Array.<ol.View3dAnimationType>}
   */
